mapper.select:
SELECT COUNT(*)
  FROM HOUSE
 WHERE HOUSE_STATUS = #{category}

mapper.insert:
INSERT INTO HOUSE(HOUSE_NO
				, HOUSE_TITLE
				, HOUSE_CONTENT
				, HOUSE_TYPE
				)
		   VALUES(SEQ_HNO.NEXTVAL
		   		, #{houseTitle}
		   		, #{houseContent}
		   		, #{houseType}
		   		)

mapper.update:
UPDATE HOUSE
   SET HOUSE_STATUS = 'N'
 WHERE HOUSE_NO = #{houseNo}

UPDATE HOUSE
   SET HOUSE_TITLE = #{houseTitle}
     , HOUSE_CONTENT = #{houseContent}
     , HOUSE_TYPE = #{houseType}
 WHERE HOUSE_NO = #{houseNo}

UPDATE HOUSE
   SET HOUSE_STATUS = #{houseStatus}
 WHERE HOUSE_NO = #{houseNo}
