				, REPORT_ID)
		   VALUES(SEQ_BNO.NEXTVAL
		   		, #{reportTitle}
		   		, #{reportContent}
		   		, #{reportType}
		   		, #{reportId})

mapper.update:
UPDATE REPORT
   SET REPORT_STATUS = 'N'
 WHERE REPORT_NO = #{reportNo}

UPDATE REPORT
   SET REPORT_TITLE = #{reportTitle}
     , REPORT_CONTENT = #{reportContent}
     , REPORT_TYPE = #{reportType}
     , REPORT_ID = #{reportId}
 WHERE REPORT_NO = #{reportNo}

UPDATE REPORT
   SET REPORT_STATUS = #{reportStatus}
 WHERE REPORT_NO = #{reportNo}
